    def load_data_to_table_barang(self):
        self.viewbarang.setRowCount(0)
        data = PB.load_all_data()
        for row_number, row_data in enumerate(data):
            self.viewbarang.insertRow(row_number)
            for column_number, data_item in enumerate(row_data):
                item = QtWidgets.QTableWidgetItem(str(data_item))
                self.viewbarang.setItem(row_number, column_number, item)
                 
