    def cari_data_pembayaran_barang(self):
        id = self.lineEdit.text() 
        data = PB.select_data_by_id_pembayaran(id)  
        if data:
            if data[0][10] == "Sudah Dibayar": 
                QtWidgets.QMessageBox.information(
                None, "Informasi", f"ID {id} sudah dibayar!"
            )
                return  
            self.label_11.setText(data[0][1])  
            self.label_12.setText(data[0][2])  
            self.label_13.setText(data[0][3])  
            self.input_alamat_tujuanbarang.setText(data[0][4])
